import { useCallback, useState } from 'react'
import './App.css'
import Login from './components/Login'
import { TodoItem } from './components/TodoItem'

const initialToDoLists = [
    {
        title: 'buy a bottle of milk',
        description: 'organic',
        author: '',
        dateCreated: '2023-10-01',
        complete: true,
        dateCompleted: '2023-10-02',
    },
    {
        title: 'close the door',
        description: '',
        author: '',
        dateCreated: '2023-08-11',
        complete: true,
        dateCompleted: '2023-08-11',
    },
    {
        title: 'submit homework',
        description: 'csc436',
        author: '',
        dateCreated: '2023-10-01',
        complete: false,
        dateCompleted: '',
    },
]

function App() {
    const [authed, setAuthed] = useState(false)
    const [name, setName] = useState('')
    const [todoList, setTodoList] = useState([])

    const onLogin = useCallback((userName) => {
        setAuthed(true)
        setName(userName)
    }, [])
    if (!authed) {
        return <Login onSubmit={onLogin} />
    }
    return (
        <div className="App">
            <h1>{name}'s Todolist</h1>
            {todoList.map((item) => (
                <TodoItem item={item} />
            ))}
        </div>
    )
}

export default App
