import React, { useCallback, useReducer, useState } from 'react'
import { v4 as uuidv4 } from 'uuid'
import './App.css'
import { Button, Modal } from 'react-bootstrap'
import Login from './components/Login'
import TodoItem from './components/TodoItem'
import Logout from './components/Logout'
import NewTodoForm from './components/NewTodoForm'
import { appReducer } from './reducer'
import { StateContext } from './contexts'

const initialToDoLists = [
    {
        id: uuidv4(),
        title: 'buy a bottle of milk',
        description: 'organic',
        author: 'Default',
        dateCreated: new Date('2020-05-12T23:50:21.817Z'),
        complete: true,
        dateCompleted: new Date('2020-05-14T13:50:21.817Z'),
    },
    {
        id: uuidv4(),
        title: 'close the door',
        description: '',
        author: 'Default',
        dateCreated: new Date('2023-10-09T13:50:21.817Z'),
        complete: true,
        dateCompleted: new Date('2023-10-10T21:50:21.817Z'),
    },
    {
        id: uuidv4(),
        title: 'submit homework',
        description: 'csc436',
        author: 'Default',
        dateCreated: new Date('2023-10-08T13:50:21.817Z'),
        complete: false,
        dateCompleted: '',
    },
]

function App() {
    const [state, dispatch] = useReducer(appReducer, {
        todoList: initialToDoLists,
        user: '',
    })
    const [show, setShow] = useState(false)

    const handleClose = () => setShow(false)
    const handleShow = () => setShow(true)

    const handleCreateTodo = (newTodo) => {
        dispatch({
            type: 'CREATE_TODO',
            newTodo,
        })
    }

    const onSubmit = useCallback((newData) => {
        handleClose()
        handleCreateTodo(newData)
    }, [])

    return (
        <StateContext.Provider value={{ state, dispatch }}>
            {state.user ? (
                <div className="App">
                    <h1>{state.user}'s Todolist</h1>
                    <div className="operations">
                        <Button onClick={handleShow}>Add New</Button>
                        <Logout />
                    </div>
                    <div className="todo-list">
                        {state.todoList.map((item) => (
                            <TodoItem item={item} key={item.id} />
                        ))}
                    </div>
                    <Modal show={show} onHide={handleClose}>
                        <Modal.Header closeButton>
                            <Modal.Title>Add a new Todo</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <NewTodoForm onSubmit={(data) => onSubmit(data)} />
                        </Modal.Body>
                    </Modal>
                </div>
            ) : (
                <Login />
            )}
        </StateContext.Provider>
    )
}

export default App
