import { useCallback, useState } from 'react'
import './App.css'
import Login from './components/Login'
import { TodoItem } from './components/TodoItem'
import Logout from './components/Logout'
import { Button, Modal } from 'react-bootstrap'
import { NewTodoForm } from './components/NewTodoForm'

const initialToDoLists = [
    {
        title: 'buy a bottle of milk',
        description: 'organic',
        author: '',
        dateCreated: '2023-10-01',
        complete: true,
        dateCompleted: '2023-10-02',
    },
    {
        title: 'close the door',
        description: '',
        author: '',
        dateCreated: '2023-08-11',
        complete: true,
        dateCompleted: '2023-08-11',
    },
    {
        title: 'submit homework',
        description: 'csc436',
        author: '',
        dateCreated: '2023-10-01',
        complete: false,
        dateCompleted: '',
    },
]

function App() {
    const [authed, setAuthed] = useState(false)
    const [name, setName] = useState('')
    const [todoList, setTodoList] = useState(initialToDoLists)
    const [show, setShow] = useState(false)

    const handleClose = () => setShow(false)
    const handleShow = () => setShow(true)

    const onLogin = useCallback((userName) => {
        setAuthed(true)
        setName(userName)
    }, [])

    const onLogOut = useCallback(() => {
        setAuthed(false)
        setName('')
    }, [])

    const onSubmit = useCallback((newData) => {
        handleClose()
        console.log('s2')
        setTodoList([...todoList, { ...newData, author: name }])
    }, [])

    // if (!authed) {
    //     return <Login onSubmit={onLogin} />
    // }

    return (
        <div className="App">
            <h1>{name}'s Todolist</h1>
            <div className="operations">
                <Button onClick={handleShow}>add new Todo</Button>
                <Logout onLogOut={onLogOut} />
            </div>
            <div className="todo-list">
                {todoList.map((item) => (
                    <TodoItem item={item} key={item.title} />
                ))}
            </div>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add a new Todo</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <NewTodoForm onSubmit={onSubmit} />
                </Modal.Body>
            </Modal>
        </div>
    )
}

export default App
